pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "contracts/Proposal.sol";

contract DAO is Ownable, ERC20 {
    bytes32 public merkleRoot;
    uint32 public proposalId;

    mapping(uint32 => address) public proposal;

    event MerkleRootUpdated(bytes32 newRoot);
    event ProposalCreated(address indexed creator, address proposalAddress);
    event AirdropCreated(address indexed creator, address airdropAddress);

    constructor(bytes32 _merkleRoot) ERC20("Gov Token", "GT") Ownable(msg.sender) { // name: Gov Token, Symbol: GT, decimals: 1
        merkleRoot = _merkleRoot;
        proposalId = 0;
    }

    function decimals() public view virtual override returns(uint8) {
        return 1;
    }

    function updateMerkleRoot(bytes32 _newMerkleRoot) external onlyOwner {
        merkleRoot = _newMerkleRoot;
        emit MerkleRootUpdated(_newMerkleRoot);
    }

    function verify(bytes32[] calldata proof, address account) public view returns (bool) {
        bytes32 leaf = keccak256(bytes(_toHexString(account)));
        return _verifyProof(proof, leaf, merkleRoot);
    }

    function _verifyProof(
        bytes32[] calldata proof,
        bytes32 leaf,
        bytes32 root
    ) internal pure returns (bool) {
        bytes32 computedHash = leaf;
        for (uint256 i = 0; i < proof.length; i++) {
            bytes32 proofElement = proof[i];
            if (computedHash <= proofElement) {
                computedHash = keccak256(abi.encodePacked(computedHash, proofElement));
            } else {
                computedHash = keccak256(abi.encodePacked(proofElement, computedHash));
            }
        }
        return computedHash == root;
    }

    function _toHexString(address addr) internal pure returns (string memory) {
        bytes memory buffer = new bytes(40);
        for (uint256 i = 0; i < 20; i++) {
            uint8 b = uint8(uint160(addr) >> (8 * (19 - i)));
            uint8 hi = b >> 4;
            uint8 lo = b & 0x0f;
            buffer[2 * i] = bytes1(hi < 10 ? hi + 0x30 : hi + 0x57);
            buffer[2 * i + 1] = bytes1(lo < 10 ? lo + 0x30 : lo + 0x57);
        }
        return string(buffer);
    }

    function createProposal(
       bytes32[] calldata proof,
       string memory _description,
       uint256 _votingPeriod,
       address _target,
       uint256 _value,
       bytes memory _calldata
    ) external returns (address) {
        require(verify(proof, msg.sender), "Not in Merkle Tree");
        address proposalAddress = deployProxy(proposalId, _description, _votingPeriod, _target, _value, _calldata);
        proposal[proposalId] = proposalAddress;
        proposalId++;
        emit ProposalCreated(msg.sender, proposalAddress);
        return proposalAddress;
    }

    function deployProxy(
       uint32 _proposalId,
       string memory _description,
       uint256 _votingPeriod,
       address _target,
       uint256 _value,
       bytes memory _calldata
    ) internal returns (address) {
        bytes memory code = abi.encodePacked(type(Proposal).creationCode, abi.encode(_proposalId, _description, _votingPeriod, _target, _value, _calldata));
        address proxy;
        assembly {
            proxy := create2(0, add(code, 0x20), mload(code), 0)
        }
        require(proxy != address(0), "Proxy deployment failed");
        return proxy;
    }

    function addReputation(uint32 _proposalId, address _receiver, uint256 _amount) external{
        require(proposal[_proposalId] == msg.sender, "Not proposal contract");
        _mint(_receiver, _amount);
    }

    // proposal execution logic
    function executeProposal(
        uint32 _proposalId,
        address _target,
        uint256 _value,
        bytes calldata _calldata,
        address _executor
    ) external {
        require(proposal[_proposalId] == msg.sender, "Not authorized proposal");
        require(address(this).balance >= _value, "DAO: insufficient ETH");

        (bool success, ) = _target.call{value: _value}(_calldata);
        require(success, "Execution failed");

        _mint(_executor, 1); // bouns
    }

    receive() external payable {}
    fallback() external payable {}
}","keccak256":"0x5804d83a98b06282be44e2ad515d68fdbcfa6921e3a16909abcd052617327909","license":"MIT"},"contracts/Proposal.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;
import "contracts/DAO.sol";
import "@fhevm/solidity/lib/FHE.sol";
import { ZamaEthereumConfig } from "@fhevm/solidity/config/ZamaConfig.sol";

contract Proposal is ZamaEthereumConfig{
    uint32 public proposalId;
    // uint64 public decryptedYesVotes;
    // uint64 public decryptedNoVotes;
    euint64 private encryptedYesVotes;
    euint64 private encryptedNoVotes;
    uint256 public commitEnd;
    uint256 public revealEnd;
    string public description;
    DAO public dao;
    // bool public decrypted;
    address public targetAddress;
    uint256 public value;
    bytes public executionCalldata;
    bool public executed;

    event VoteCommitted(address committer, bytes32 commitment);
    event VoteRevealed(address revealer);
    event ProposalExecuted(address executor);

    // user commitment：proposalId => address => commitment (hash(support + salt))
    mapping(address => bytes32) public commitments;
    mapping(address => bool) public revealed;

    constructor(uint32 _proposalId,
      string memory _description,
      uint256 _vtPeriod,
      address _target,
      uint256 _value,
      bytes memory _calldata
    ) {
        proposalId = _proposalId;
        revealEnd = block.timestamp + _vtPeriod;
        commitEnd = block.timestamp + _vtPeriod/2;
        description = _description;
        targetAddress = _target;
        value = _value;
        executionCalldata = _calldata;
        encryptedYesVotes = FHE.asEuint64(0);
        encryptedNoVotes = FHE.asEuint64(0);
        dao = DAO(payable(msg.sender));

        FHE.allowThis(encryptedYesVotes);
        FHE.allowThis(encryptedNoVotes);
    }

    function canCommit() public view returns (bool) {
        return block.timestamp <= commitEnd;
    }

    function canReveal() public view returns (bool) {
        return block.timestamp > commitEnd && block.timestamp <= revealEnd;
    }

    function isActive() public view returns (bool) {
        return block.timestamp <= revealEnd;
    }

    function getEncryptedYesVotes() public view returns (euint64) {
        require(block.timestamp > revealEnd, "Can get encrypted data until voting ended");
        return encryptedYesVotes;
    }

    function getEncryptedNoVotes() public view returns (euint64) {
        require(block.timestamp > revealEnd, "Can get encrypted data until voting ended");
        return encryptedNoVotes;
    }

    // commitment：submit hash(support + salt); support => even num(dis)/odd num(agree)
    // salt: private random bytes32
    function commitVote(bytes32[] calldata proof, bytes32 commitment) external {
        require(isActive(), "Proposal not active");
        require(dao.verify(proof, msg.sender), "Only member can participate");
        require(block.timestamp <= commitEnd, "Commit period ended");
        require(commitments[msg.sender] == 0, "Already committed");

        commitments[msg.sender] = commitment;
        emit VoteCommitted(msg.sender, commitment);
    }

    // reveal：submit the support and salt what you committed, and reptation be increased
    function revealVote(externalEuint8 esupport, bytes32 salt, bytes calldata attestation) external {
        require(block.timestamp > commitEnd, "Commit period not ended");
        require(block.timestamp <= revealEnd, "Proposal not active");
        require(!revealed[msg.sender], "Already revealed");

        euint8 support = FHE.fromExternal(esupport, attestation);
        bytes32 commitment = keccak256(abi.encodePacked(support, salt));
        require(commitments[msg.sender] == commitment, "Invalid reveal");

        ebool eq0 = FHE.eq(support, FHE.asEuint8(0));
        ebool gt0 = FHE.eq(support, FHE.asEuint8(1));
        uint64 weight = uint64(1 + dao.balanceOf(msg.sender));
        euint64 eweight = FHE.asEuint64(weight);
        encryptedYesVotes = FHE.select(gt0, FHE.add(encryptedYesVotes, eweight), FHE.add(encryptedYesVotes, FHE.asEuint64(0)));
        encryptedNoVotes = FHE.select(eq0, FHE.add(encryptedNoVotes, eweight), FHE.add(encryptedNoVotes, FHE.asEuint64(0)));

        revealed[msg.sender] = true;
        dao.addReputation(proposalId, msg.sender, 1);

        FHE.allowThis(encryptedYesVotes);
        FHE.allowThis(encryptedNoVotes);
        FHE.makePubliclyDecryptable(encryptedYesVotes);
        FHE.makePubliclyDecryptable(encryptedNoVotes);

        emit VoteRevealed(msg.sender);
    }

    // proposal execution logic
    function execute(
        bytes memory abiEncodedResult,
        bytes memory decryptionProof
    ) external {
        require(block.timestamp > revealEnd, "Voting not ended");
        require(!executed, "Already executed");

        bytes32[] memory cts = new bytes32[](2);
        cts[0] = FHE.toBytes32(this.getEncryptedYesVotes());
        cts[1] = FHE.toBytes32(this.getEncryptedNoVotes());

        FHE.checkSignatures(cts, abiEncodedResult, decryptionProof);
        (uint64 decodedYesVotes, uint64 decodedNoVotes) = abi.decode(
            abiEncodedResult,
            (uint64, uint64)
        );

        require(decodedYesVotes > decodedNoVotes, "Proposal did not pass");
        require(address(dao).balance >= value, "DAO: insufficient ETH");

        executed = true;

        dao.executeProposal(proposalId, targetAddress, value, executionCalldata, msg.sender);
        emit ProposalExecuted(msg.sender);
    }

